<script setup lang="ts">
import { timeline } from "motion"
import HeroImage from "@/assets/fx.svg"
import { TimelineDefinition } from "@motionone/dom/types/timeline/types"

const color = ref("rgb(61 207 142)")
const draw = (progress: number) => ({
  strokeDashoffset: 1 - progress,
  visibility: "visible",
})
const fill = (color: string) => ({
  fill: color,
})

const lineAnimation = (i: number) => {
  const sequence: TimelineDefinition = [
    [`#line${i}`, draw(0), { duration: 1 }],
    [`#line${i}`, draw(1), { duration: 2 }],
    [`#line${i}`, fill(color.value), { duration: 1 }],
    [`#line${i}`, { opacity: 0 }, { duration: 2.5 }],
  ]
  timeline(sequence)
}

onMounted(() => {
  for (let i = 1; i <= 9; i++) {
    lineAnimation(i)
  }
})
</script>

